class DateConverter {
    /**
     * Converts dates from European format (DD/MM/YYYY) to American format (MM/DD/YYYY) within a given text.
     * Only valid dates are converted. Uses Groovy's simple date parsing to validate dates.
     * @param text The input text containing potential dates in European format
     * @return The text with valid European dates converted to American format
     */
    static String convertDatesEuToUs(String text) {
        // Regular expression pattern to match dates in DD/MM/YYYY format
        def datePattern = ~/\b(\d{2})\/(\d{2})\/(\d{4})\b/

        // Use a closure to process each matched date
        text.replaceAll(datePattern) { fullMatch, dd, mm, yyyy ->
            if (isValidDate(dd, mm, yyyy)) {
                "$mm/$dd/$yyyy"  // Convert to US format if valid
            } else {
                fullMatch  // Keep original if invalid
            }
        }
    }

    /**
     * Validates if the given day, month, and year form a valid date.
     * @param day Day as string (DD)
     * @param month Month as string (MM)
     * @param year Year as string (YYYY)
     * @return true if the date is valid, false otherwise
     */
    static boolean isValidDate(String day, String month, String year) {
        try {
            // Parse the date string to validate it
            def dateStr = "${day.padLeft(2, '0')}/${month.padLeft(2, '0')}/${year}"
            def format = new java.text.SimpleDateFormat("dd/MM/yyyy")
            format.setLenient(false)  // This makes the parser strict about dates
            format.parse(dateStr)
            return true
        } catch (Exception e) {
            return false
        }
    }
}

