class StringConverter {
    /**
     * Converts a given string to snake_case.
     * Words are lowercase and separated by underscores.
     * Words can be separated by spaces, hyphens, or joined in camelCase or PascalCase.
     *
     * @param s The input string to convert
     * @return The converted string in snake_case format
     */
    static String toSnakeCase(String s) {
        if (s == null || s.isEmpty()) {
            return s
        }

        // Handle camelCase and PascalCase by inserting underscores before uppercase letters
        def converted = s.replaceAll(/([a-z0-9])([A-Z])/, '$1_$2')
                        .replaceAll(/([A-Z])([A-Z][a-z0-9])/, '$1_$2')

        // Replace spaces and hyphens with underscores
        converted = converted.replaceAll(/[\s\-]+/, '_')

        // Convert entire string to lowercase
        converted = converted.toLowerCase()

        // Remove leading and trailing underscores, if any
        converted = converted.replaceAll(/^_+|_+$/, '')

        return converted
    }
}

