void testCanFormPalindrome() {
    assert MainClass.canFormPalindrome("aabb", 0) == true
    assert MainClass.canFormPalindrome("aabb", 1) == true
    assert MainClass.canFormPalindrome("aabbc", 1) == true
    assert MainClass.canFormPalindrome("aabbcc", 2) == true
    assert MainClass.canFormPalindrome("abcde", 2) == false
    assert MainClass.canFormPalindrome("abcde", 3) == false
}

