class StringFilter {
    /**
     * Filters a list of strings based on minimum length and presence of a given substring.
     * 
     * @param strings List of strings to be filtered
     * @param minLength Minimum length requirement for strings to be included
     * @param substring Substring that must be present in the strings to be included
     * @return List of strings that meet both criteria (length and substring presence)
     */
    static List<String> filterStrings(List<String> strings, int minLength, String substring) {
        return strings.findAll { it -> 
            it.size() >= minLength && it.contains(substring) 
        }
    }
}

assertions:
void testStringFilter() {
    // Test Case 1: Multiple matches
    assert StringFilter.filterStrings(["apple", "banana", "cherry", "date"], 5, "a") == ["apple", "banana"]

    // Test Case 2: Some matches
    assert StringFilter.filterStrings(["hello", "world", "groovy"], 6, "o") == ["groovy"]

    // Test Case 3: No substring match
    assert StringFilter.filterStrings(["short", "tiny", "small"], 4, "x") == []

    // Test Case 4: No length match
    assert StringFilter.filterStrings(["longword", "short", "medium"], 10, "e") == []

    // Test Case 5: Empty input
    assert StringFilter.filterStrings([], 3, "a") == []
}

testStringFilter()

