class BuildingDistanceCalculator {
    // Calculates the maximum distance from any empty lot ('.') to the nearest building ('B') in a grid.
    // Uses BFS (Breadth-First Search) starting from all buildings simultaneously.
    // Returns -1 if there are no empty lots or if some empty lots are unreachable from buildings.
    static int maxDistanceToBuilding(List<List<String>> grid) {
        if (grid.isEmpty()) return -1

        int M = grid.size()
        int N = grid[0].size()
        def queue = [] as ArrayDeque
        def dist = (0..<M).collect { (0..<N).collect { Integer.MAX_VALUE } }

        // Initialize queue and distances for building locations
        for (int i = 0; i < M; i++) {
            for (int j = 0; j < N; j++) {
                if (grid[i][j] == 'B') {
                    queue.add([i, j])
                    dist[i][j] = 0
                }
            }
        }

        // Directions for the 4 neighbors (up, down, left, right)
        def directions = [[0, 1], [1, 0], [0, -1], [-1, 0]]

        // Perform BFS from each building
        while (!queue.isEmpty()) {
            def (x, y) = queue.poll()
            directions.each { dx, dy ->
                int nx = x + dx
                int ny = y + dy
                if (nx >= 0 && nx < M && ny >= 0 && ny < N && dist[nx][ny] == Integer.MAX_VALUE) {
                    dist[nx][ny] = dist[x][y] + 1
                    queue.add([nx, ny])
                }
            }
        }

        // Calculate the maximum distance from any empty lot to the nearest building
        int maxDist = -1
        for (int i = 0; i < M; i++) {
            for (int j = 0; j < N; j++) {
                if (grid[i][j] == '.') {
                    maxDist = Math.max(maxDist, dist[i][j])
                }
            }
        }

        // If maxDist remains -1, there were no empty lots
        // If any empty lot has MAX_VALUE, it means it's unreachable from any building
        return (maxDist == Integer.MAX_VALUE) ? -1 : maxDist
    }
}
void testBuildingDistanceCalculator() {
    // Test case 1: Simple grid with buildings and empty lots
    def grid1 = [
        ['B', '.', '.'],
        ['.', '.', '.'],
        ['.', '.', 'B']
    ]
    assert BuildingDistanceCalculator.maxDistanceToBuilding(grid1) == 2

    // Test case 2: All buildings
    def grid2 = [
        ['B', 'B'],
        ['B', 'B']
    ]
    assert BuildingDistanceCalculator.maxDistanceToBuilding(grid2) == -1

    // Test case 3: All empty lots (should return -1)
    def grid3 = [
        ['.', '.'],
        ['.', '.']
    ]
    assert BuildingDistanceCalculator.maxDistanceToBuilding(grid3) == -1

    // Test case 4: Some unreachable empty lots
    def grid4 = [
        ['B', '.', '.'],
        ['.', '.', '.'],
        ['.', '.', '.']
    ]
    assert BuildingDistanceCalculator.maxDistanceToBuilding(grid4) == 4

    // Test case 5: Single building
    def grid5 = [
        ['.', '.', '.'],
        ['.', 'B', '.'],
        ['.', '.', '.']
    ]
    assert BuildingDistanceCalculator.maxDistanceToBuilding(grid5) == 2
}

