class HeightInterpolator {
    /**
     * Interpolates missing height values (represented as null) in a list using linear interpolation.
     * For edge cases (missing values at start or end), uses the nearest valid value.
     *
     * @param heights List of height values with possible nulls representing missing data
     * @return List with nulls replaced by interpolated values
     */
    static List<Double> interpolateMissingHeights(List<Double> heights) {
        def n = heights.size()
        def result = heights.collect() // Create a copy of the original list

        for (int i = 0; i < n; i++) {
            if (heights[i] == null) {
                // Find nearest previous and next non-null values
                int prevIdx = i - 1
                int nextIdx = i + 1

                while (prevIdx >= 0 && heights[prevIdx] == null) {
                    prevIdx--
                }
                while (nextIdx < n && heights[nextIdx] == null) {
                    nextIdx++
                }

                if (prevIdx < 0) {
                    // No previous value found, use next value
                    result[i] = heights[nextIdx]
                } else if (nextIdx >= n) {
                    // No next value found, use previous value
                    result[i] = heights[prevIdx]
                } else {
                    // Linear interpolation between previous and next values
                    def prevValue = heights[prevIdx]
                    def nextValue = heights[nextIdx]
                    def distance = nextIdx - prevIdx
                    result[i] = prevValue + (nextValue - prevValue) * (i - prevIdx) / distance
                }
            }
        }

        return result
    }
}

