    // All values missing (edge case)
    assert HeightInterpolator.interpolateMissingHeights([null, null, null]) == [null, null, null]
    // Complex case with multiple missing values
    assert HeightInterpolator.interpolateMissingHeights([10.0, null, 20.0, null, null, 50.0, null, 70.0]) == [10.0, 15.0, 20.0, 30.0, 40.0, 50.0, 60.0, 70.0]
