class TextEditor {
    // The main class implementing a simple text editor with undo/redo capabilities

    String content = ""
    List history = []
    List redoStack = []

    // Inserts text at the specified position in the content
    // position: 0-based index where to insert the text
    // text: the string to be inserted
    void insertText(int position, String text) {
        // Record the operation for undo capability
        history.add(["insert", position, text])

        // Clear redo stack since we're making a new change
        redoStack.clear()

        // Perform the insertion
        content = content.substring(0, position) + text + content.substring(position)
    }

    // Deletes text from the content starting at given position with specified length
    // position: starting index of deletion
    // length: number of characters to delete
    void deleteText(int position, int length) {
        // Get the text that will be deleted for undo capability
        String deletedText = content.substring(position, Math.min(position + length, content.length()))
        history.add(["delete", position, deletedText])

        // Clear redo stack
        redoStack.clear()

        // Perform the deletion
        content = content.substring(0, position) + content.substring(Math.min(position + length, content.length()))
    }

    // Undoes the last operation performed on the content
    void undo() {
        if (history) {
            def lastOperation = history.remove(history.size() - 1)
            def opType = lastOperation[0]
            def position = lastOperation[1]
            def text = lastOperation[2]

            if (opType == "insert") {
                // Undo insert by deleting the inserted text
                content = content.substring(0, position) + content.substring(position + text.length())
            } else if (opType == "delete") {
                // Undo delete by reinserting the deleted text
                content = content.substring(0, position) + text + content.substring(position)
            }

            // Add to redo stack
            redoStack.add(lastOperation)
        }
    }

    // Redoes the last undone operation
    void redo() {
        if (redoStack) {
            def lastOperation = redoStack.remove(redoStack.size() - 1)
            def opType = lastOperation[0]
            def position = lastOperation[1]
            def text = lastOperation[2]

            if (opType == "insert") {
                // Redo insert
                content = content.substring(0, position) + text + content.substring(position)
            } else if (opType == "delete") {
                // Redo delete
                content = content.substring(0, position) + content.substring(position + text.length())
            }

            // Add back to history
            history.add(lastOperation)
        }
    }

    // Returns the current content of the editor
    String getContent() {
        return content
    }
}

