class RLECodec {
    /**
     * Compresses the given text using Run-Length Encoding (RLE) algorithm.
     * 
     * @param text Input string to be compressed.
     * @return Compressed string using RLE.
     */
    static String rleCompress(String text) {
        if (!text) {
            return ""
        }

        def compressed = []
        int count = 1
        char currentChar = text.charAt(0)

        for (int i = 1; i < text.length(); i++) {
            if (text.charAt(i) == currentChar) {
                count++
            } else {
                compressed.add("${count}${currentChar}")
                currentChar = text.charAt(i)
                count = 1
            }
        }
        compressed.add("${count}${currentChar}")
        return compressed.join('')
    }
}

