class GridPathCalculator {
    /**
     * Calculates the number of distinct paths from the top-left corner to the bottom-right corner
     * in a grid with blocked cells.
     *
     * @param rows Number of rows in the grid
     * @param cols Number of columns in the grid
     * @param blockedCells List of blocked cell coordinates (1-based indices)
     * @return Number of distinct paths, or 0 if destination is blocked or unreachable
     */
    static int countUniquePaths(int rows, int cols, List<List<Integer>> blockedCells) {
        // Initialize a matrix to store path counts
        int[][] dp = new int[rows][cols]

        // Mark blocked cells (convert to 0-based indices)
        blockedCells.each { cell ->
            int x = cell[0] - 1
            int y = cell[1] - 1
            if (x < rows && y < cols) {
                dp[x][y] = -1
            }
        }

        // Initialize starting position if not blocked
        if (dp[0][0] != -1) {
            dp[0][0] = 1
        }

        // Fill the DP table
        for (int i = 0; i < rows; i++) {
            for (int j = 0; j < cols; j++) {
                if (dp[i][j] == -1) continue

                if (i > 0 && dp[i-1][j] != -1) {
                    dp[i][j] += dp[i-1][j]
                }
                if (j > 0 && dp[i][j-1] != -1) {
                    dp[i][j] += dp[i][j-1]
                }
            }
        }

        return (dp[rows-1][cols-1] != -1) ? dp[rows-1][cols-1] : 0
    }
}

