class DijkstraSolver {
    // Implements Dijkstra's algorithm to find the shortest path between two nodes in a graph
    // with non-negative edge weights. Returns -1 if no path exists.
    static int dijkstra(int n, List<List<Integer>> edges, int start, int target) {
        // Build adjacency list representation of the graph
        Map<Integer, List<List<Integer>>> graph = [:]
        (1..n).each { graph[it] = [] }

        edges.each { edge ->
            def u = edge[0], v = edge[1], w = edge[2]
            graph[u] << [v, w]
            graph[v] << [u, w]
        }

        // Priority queue (min-heap) using Groovy's list with custom comparator
        def pq = new PriorityQueue<List<Integer>>(Comparator.comparingInt { it[0] })
        pq.add([0, start])

        // Distance dictionary initialized to infinity
        Map<Integer, Integer> dist = [:]
        (1..n).each { dist[it] = Integer.MAX_VALUE }
        dist[start] = 0

        Set<Integer> visited = new HashSet<>()

        while (!pq.isEmpty()) {
            def (currentDist, u) = pq.poll()

            if (u in visited) continue
            visited.add(u)

            if (u == target) return currentDist

            graph[u].each { neighbor, weight ->
                if (!(neighbor in visited)) {
                    def newDist = currentDist + weight
                    if (newDist < dist[neighbor]) {
                        dist[neighbor] = newDist
                        pq.add([newDist, neighbor])
                    }
                }
            }
        }

        return -1
    }
}

