class MainClass {
    // findMaxSubarray finds the contiguous subarray with the maximum sum in a given list of integers.
    // It uses a divide and conquer approach to solve the problem efficiently.
    // Returns a list containing the start index, end index, and the sum of the maximum subarray.
    // If the input list is empty, it returns [0, 0, 0].
    static List<Integer> findMaxSubarray(List<Integer> arr) {
        if (!arr) {
            return [0, 0, 0]
        }
        return findMaxSubarrayHelper(arr, 0, arr.size() - 1)
    }

    // Helper function for findMaxSubarray that recursively finds the maximum subarray.
    // It divides the array into left and right halves, then combines the results.
    static List<Integer> findMaxSubarrayHelper(List<Integer> arr, int low, int high) {
        if (low == high) {
            return [low, high, arr[low]]
        }

        int mid = (low + high) / 2
        def leftResult = findMaxSubarrayHelper(arr, low, mid)
        def rightResult = findMaxSubarrayHelper(arr, mid + 1, high)
        def crossResult = maxCrossingSubarray(arr, low, mid, high)

        if (leftResult[2] >= rightResult[2] && leftResult[2] >= crossResult[2]) {
            return leftResult
        } else if (rightResult[2] >= leftResult[2] && rightResult[2] >= crossResult[2]) {
            return rightResult
        } else {
            return crossResult
        }
    }

    // maxCrossingSubarray finds the maximum subarray that crosses the midpoint of the array.
    // It searches for the maximum sum on both the left and right sides of the midpoint.
    static List<Integer> maxCrossingSubarray(List<Integer> arr, int low, int mid, int high) {
        int leftSum = Integer.MIN_VALUE
        int sum = 0
        int crossLow = mid
        for (int i = mid; i >= low; i--) {
            sum += arr[i]
            if (sum > leftSum) {
                leftSum = sum
                crossLow = i
            }
        }

        int rightSum = Integer.MIN_VALUE
        sum = 0
        int crossHigh = mid + 1
        for (int j = mid + 1; j <= high; j++) {
            sum += arr[j]
            if (sum > rightSum) {
                rightSum = sum
                crossHigh = j
            }
        }

        return [crossLow, crossHigh, leftSum + rightSum]
    }
}

