class BallDropSimulator {
    /**
     * Simulates the stopping position of a ball dropped from the first building.
     * The ball stops when it hits a building equal or taller than the starting building.
     * 
     * @param heights List of building heights
     * @return Index where the ball stops, or -1 if it doesn't stop
     */
    static int findBallStop(List<Integer> heights) {
        if (heights.isEmpty()) {
            return -1
        }
        int startHeight = heights[0]

        for (int i = 1; i < heights.size(); i++) {
            if (heights[i] >= startHeight) {
                return i
            }
        }
        return -1
    }
}

