class MainClass {
    /**
     * Finds the minimum possible value of the maximum difference between any two elements
     * in a subarray of length exactly K.
     *
     * @param arr The input array of numbers.
     * @param N The length of the input array.
     * @param K The length of the subarray.
     * @return The minimum possible value of the maximum difference in any subarray of length K.
     */
    static def minMaxDiffInSubarray(List<Integer> arr, int N, int K) {
        if (K == 1) {
            return 0
        }

        // Sort the array to easily find the minimum and maximum in any subarray
        def sortedArr = arr.sort()
        def minMaxDiff = Integer.MAX_VALUE

        // Iterate through all possible subarrays of length K
        for (int i = 0; i <= N - K; i++) {
            def currentDiff = sortedArr[i + K - 1] - sortedArr[i]
            if (currentDiff < minMaxDiff) {
                minMaxDiff = currentDiff
            }
        }

        return minMaxDiff
    }
}

