class MainClass {
    /**
     * Calculates the minimum absolute difference between any two different values in a list.
     * The list is first sorted to simplify the calculation.
     * If the list has fewer than 2 elements, returns 0 as no difference can be calculated.
     *
     * @param values List of numerical values
     * @return Minimum absolute difference between any two consecutive values in the sorted list
     */
    static def minAbsoluteDifference(List<Double> values) {
        if (values.size() < 2) {
            return 0.0
        }
        values.sort()
        def minDiff = Double.MAX_VALUE
        for (int i = 1; i < values.size(); i++) {
            def currentDiff = Math.abs(values[i] - values[i - 1])
            if (currentDiff < minDiff) {
                minDiff = currentDiff
            }
        }
        return minDiff
    }
}

