    // Finds the maximum subarray sum and the starting and ending indices of the subarray.
    // Uses Kadane's algorithm to efficiently compute the solution in O(n) time.
    // If the input list is empty, it returns 0 for the sum and -1 for both indices.
    static def maxSubarraySum(List<Integer> arr) {
        if (arr.isEmpty()) {
            return [0, -1, -1]
        }

        int maxSum = arr[0]
        int currentSum = arr[0]
        int start = 0
        int end = 0
        int s = 0

        for (int i = 1; i < arr.size(); i++) {
            if (currentSum < 0) {
                currentSum = arr[i]
                s = i
            } else {
                currentSum += arr[i]
            }

            if (currentSum > maxSum) {
                maxSum = currentSum
                start = s
                end = i
            }
        }

        return [maxSum, start, end]
    }
}

