class PerfectSequenceChecker {
    /**
     * Determines if a sequence is a perfect sequence (arithmetic sequence with constant difference).
     * A perfect sequence is defined as a sequence where the difference between consecutive elements is constant.
     * 
     * @param n The length of the sequence (must be at least 2 for a valid check)
     * @param seq The sequence of integers to check
     * @return 'YES' if the sequence is perfect, 'NO' otherwise
     */
    static String isPerfectSequence(int n, List<Integer> seq) {
        if (n < 2) {
            return "NO"
        }

        // Calculate the common difference between first two elements
        int commonDiff = seq[1] - seq[0]

        // Check if this difference holds for all consecutive elements
        for (int i = 2; i < n; i++) {
            if (seq[i] - seq[i - 1] != commonDiff) {
                return "NO"
            }
        }

        return "YES"
    }
}

