class MatrixChainMultiplication {
    // matrixChainOrder calculates the minimum number of scalar multiplications needed to multiply a chain of matrices.
    // It uses dynamic programming to fill a matrix with the minimum costs and a solution matrix to store the optimal split points.
    // Returns a tuple containing the cost matrix and the solution matrix.
    static def matrixChainOrder(List<Integer> array) {
        int n = array.size() - 1
        def matrix = new int[n + 1][n + 1]
        def sol = new int[n + 1][n + 1]

        for (int l = 2; l <= n; l++) {
            for (int i = 1; i <= n - l + 1; i++) {
                int j = i + l - 1
                matrix[i][j] = Integer.MAX_VALUE
                for (int k = i; k < j; k++) {
                    int q = matrix[i][k] + matrix[k + 1][j] + array[i - 1] * array[k] * array[j]
                    if (q < matrix[i][j]) {
                        matrix[i][j] = q
                        sol[i][j] = k
                    }
                }
            }
        }

        return [matrix, sol]
    }

    // printOptimalSolution prints the optimal parenthesization of the matrix chain multiplication.
    // It recursively traverses the solution matrix to construct the parenthesized string.
    static void printOptimalSolution(int[][] sol, int i, int j) {
        if (i == j) {
            print("A${i}")
        } else {
            print("(")
            printOptimalSolution(sol, i, sol[i][j])
            print(" ")
            printOptimalSolution(sol, sol[i][j] + 1, j)
            print(")")
        }
    }
}

