    /**
     * Counts the number of set bits (1-bits) in the binary representation of a non-negative integer.
     * 
     * @param num A non-negative integer.
     * @return The count of set bits in the binary representation of 'num'.
     */
    static int countSetBits(int num) {
        int count = 0
        while (num != 0) {
            count += num & 1
            num = num >>> 1 // Using unsigned right shift to handle all cases
        }
        return count
    }
}

