class MainClass {
    // Calculates the maximum height difference within K moves from any starting cell in a grid.
    // Uses BFS to explore all possible moves up to K steps from each starting cell.
    // For each BFS, it tracks the minimum and maximum height encountered.
    // Returns the maximum difference found across all starting cells.
    static int maxHeightDifference(List<List<Integer>> grid, int K) {
        int N = grid.size()
        int M = grid[0].size()
        def directions = [[-1, 0], [1, 0], [0, -1], [0, 1]]
        int maxDiff = 0

        // Nested BFS function to explore K moves from a starting cell
        def bfs = { startX, startY ->
            def queue = new LinkedList()
            queue.add([startX, startY, 0])
            int minHeight = grid[startX][startY]
            int maxHeight = grid[startX][startY]
            def visited = new HashSet()
            visited.add("${startX},${startY}")

            while (!queue.isEmpty()) {
                def (x, y, moves) = queue.poll()
                if (moves < K) {
                    directions.each { dx, dy ->
                        int nx = x + dx
                        int ny = y + dy
                        String key = "${nx},${ny}"
                        if (nx >= 0 && nx < N && ny >= 0 && ny < M && !visited.contains(key)) {
                            visited.add(key)
                            queue.add([nx, ny, moves + 1])
                            minHeight = Math.min(minHeight, grid[nx][ny])
                            maxHeight = Math.max(maxHeight, grid[nx][ny])
                        }
                    }
                }
            }
            return maxHeight - minHeight
        }

        // Iterate over each cell in the grid to start BFS
        for (int i = 0; i < N; i++) {
            for (int j = 0; j < M; j++) {
                maxDiff = Math.max(maxDiff, bfs(i, j))
            }
        }

        return maxDiff
    }
}

