class MainClass {
    // findMissingElement identifies the missing integer in a list of consecutive integers.
    // It calculates the expected sum of the consecutive integers and subtracts the actual sum of the list to find the missing element.
    // Assumes the list contains unique consecutive integers with exactly one integer missing.
    static int findMissingElement(List<Integer> numbers) {
        int n = numbers.size()
        int totalNumElements = n + 1
        int sumOfElements = numbers.sum()

        int firstElement = numbers.min()
        int lastElement = numbers.max()

        int expectedSum = (totalNumElements * (firstElement + lastElement)) / 2

        int missingElement = expectedSum - sumOfElements

        return missingElement
    }
}

