class BalancedStringChecker {
    /**
     * Determines if the input string is balanced considering '(' as left parenthesis,
     * ')' as right parenthesis, and '*' as a wildcard that can be treated as '(', ')', or empty.
     * 
     * @param s The input string to check for balance
     * @return true if the string is balanced, false otherwise
     */
    static boolean isBalancedString(String s) {
        int leftBalance = 0
        int rightBalance = 0

        // Check from left to right treating '*' as '('
        for (char c : s) {
            if (c == '(' || c == '*') {
                leftBalance++
            } else {
                leftBalance--
            }

            if (leftBalance < 0) {
                return false
            }
        }

        // Check from right to left treating '*' as ')'
        for (int i = s.length() - 1; i >= 0; i--) {
            char c = s.charAt(i)
            if (c == ')' || c == '*') {
                rightBalance++
            } else {
                rightBalance--
            }

            if (rightBalance < 0) {
                return false
            }
        }

        return true
    }
}

