class TaskDistributor {
    /**
     * Distributes tasks across given time slots.
     *
     * @param tasks List of maps where each map contains 'name' (String) and 'duration' (Integer)
     * @param timeSlots List of maps where each map contains 'day' (String) and 'availableTime' (Integer)
     * @return Map where keys are days (String) and values are lists of task assignments (each a map with 'name' and 'duration')
     */
    static Map<String, List<Map>> distributeTasks(List<Map> tasks, List<Map> timeSlots) {
        Map<String, List<Map>> distribution = [:]
        int currentTaskIndex = 0
        int remainingDuration = tasks ? tasks[0].duration : 0

        timeSlots.each { timeSlot ->
            String day = timeSlot.day
            int availableTime = timeSlot.availableTime

            if (!distribution.containsKey(day)) {
                distribution[day] = []
            }

            while (availableTime > 0 && currentTaskIndex < tasks.size()) {
                Map currentTask = tasks[currentTaskIndex]
                String taskName = currentTask.name
                int taskDuration = currentTask.duration

                if (remainingDuration <= availableTime) {
                    distribution[day] << [name: taskName, duration: remainingDuration]
                    availableTime -= remainingDuration
                    currentTaskIndex++
                    if (currentTaskIndex < tasks.size()) {
                        remainingDuration = tasks[currentTaskIndex].duration
                    }
                } else {
                    distribution[day] << [name: taskName, duration: availableTime]
                    remainingDuration -= availableTime
                    availableTime = 0
                }
            }
        }

        return distribution
    }
}

