class StudentSorter {
    /**
     * Sorts a list of student records first by their house, then by their name.
     * Each student record is expected to be a list/tuple where:
     * - First element is student name (String)
     * - Second element is house name (String)
     * @param records List of student records to be sorted
     * @return A new list containing the sorted student records
     */
    static List<List<String>> sortStudents(List<List<String>> records) {
        // Sort first by house (second element), then by name (first element)
        return records.sort { a, b ->
            a[1] <=> b[1] ?: a[0] <=> b[0]
        }
    }
}

