class MainClass {
    // maxProfit calculates the maximum profit by selecting at most k projects from the list of profits.
    // It sorts the list in descending order and selects the top k profits.
    // If the list size is less than k, it selects all available profits.
    // Returns the sum of the selected profits.
    static def maxProfit(List<Integer> profits, int k) {
        // Sort the profits in descending order
        def sortedProfits = profits.sort().reverse()

        // Select the top k profits or all available if less than k
        def selectedProfits = sortedProfits.take(k)

        // Return the sum of selected profits
        return selectedProfits.sum()
    }
}

