class Base7Addition {
    // Performs addition of numbers in base-7
    // Takes a list of number pairs (as strings) and returns their sums in base-7
    static List<String> base7Addition(List<List<String>> pairs) {
        List<String> results = []

        pairs.each { pair ->
            def a = pair[0]
            def b = pair[1]

            // Convert base-7 strings to base-10 integers
            def aBase10 = Integer.parseInt(a, 7)
            def bBase10 = Integer.parseInt(b, 7)

            // Perform addition in base-10
            def sumBase10 = aBase10 + bBase10

            // Convert the sum back to base-7
            def sumBase7 = ''
            def temp = sumBase10

            if (temp == 0) {
                sumBase7 = '0'
            } else {
                while (temp > 0) {
                    sumBase7 = (temp % 7).toString() + sumBase7
                    temp = temp.intdiv(7)
                }
            }

            results.add(sumBase7)
        }

        return results
    }
}

