class MainClass {
    // Represents a node in the Red-Black Tree
    static class RBNode {
        def data
        def count = 1  // Subtree node count
        def color = 'red'  // New nodes are red by default
        RBNode left
        RBNode right
        RBNode parent

        RBNode(data, count = 1) {
            this.data = data
            this.count = count
        }
    }

    // Represents a Red-Black Tree with subtree count maintenance
    static class RBTree {
        final RBNode NIL_LEAF  // Sentinel NIL leaf
        RBNode root

        RBTree() {
            this.NIL_LEAF = new RBNode(null, 0)
            this.NIL_LEAF.color = 'black'
            this.root = this.NIL_LEAF
        }

        // Inserts a node into the Red-Black Tree and maintains properties
        void insert(RBNode node) {
            node.left = node.right = this.NIL_LEAF
            node.color = 'red'
            this.insertNode(this.root, node)
            this.fixInsert(node)
        }

        // Helper method for node insertion
        private void insertNode(RBNode root, RBNode node) {
            if (root == this.NIL_LEAF) {
                if (this.root == this.NIL_LEAF) {  // For the root node
                    this.root = node
                    node.color = 'black'
                }
                return
            }

            if (node.data < root.data) {
                if (root.left == this.NIL_LEAF) {
                    root.left = node
                    node.parent = root
                } else {
                    insertNode(root.left, node)
                }
            } else {
                if (root.right == this.NIL_LEAF) {
                    root.right = node
                    node.parent = root
                } else {
                    insertNode(root.right, node)
                }
            }

            root.count += 1  // Increment count of nodes in subtree
        }

        // Fixes the Red-BBlack Tree properties after insertion
        private void fixInsert(RBNode k) {
            while (k != this.root && k.parent.color == 'red') {
                if (k.parent == k.parent.parent.left) {
                    def u = k.parent.parent.right
                    if (u.color == 'red') {
                        k.parent.color = 'black'
                        u.color = 'black'
                        k.parent.parent.color = 'red'
                        k = k.parent.parent
                    } else {
                        if (k == k.parent.right) {
                            k = k.parent
                            leftRotate(k)
                        }
                        k.parent.color = 'black'
                        k.parent.parent.color = 'red'
                        rightRotate(k.parent.parent)
                    }
                } else {
                    def u = k.parent.parent.left
                    if (u.color == 'red') {
                        k.parent.color = 'black'
                        u.color = 'black'
                        k.parent.parent.color = 'red'
                        k = k.parent.parent
                    } else {
                        if (k == k.parent.left) {
                            k = k.parent
                            rightRotate(k)
                        }
                        k.parent.color = 'black'
                        k.parent.parent.color = 'red'
                        leftRotate(k.parent.parent)
                    }
                }
            }
            this.root.color = 'black'
        }

        // Performs a left rotation around the given node
        private void leftRotate(RBNode x) {
            def y = x.right
            x.right = y.left
            if (y.left != this.NIL_LEAF) {
                y.left.parent = x
            }
            y.parent = x.parent
            if (x.parent == null) {
                this.root = y
            } else if (x == x.parent.left) {
                x.parent.left = y
            } else {
                x.parent.right = y
            }
            y.left = x
            x.parent = y

            y.count = x.count
            x.count = x.left.count + x.right.count + 1
        }

        // Performs a right rotation around the given node
        private void rightRotate(RBNode x) {
            def y = x.left
            x.left = y.right
            if (y.right != this.NIL_LEAF) {
                y.right.parent = x
            }
            y.parent = x.parent
            if (x.parent == null) {
                this.root = y
            } else if (x == x.parent.right) {
                x.parent.right = y
            } else {
                x.parent.left = y
            }
            y.right = x
            x.parent = y

            y.count = x.count
            x.count = x.left.count + x.right.count + 1
        }

        // Finds the k-th smallest element in the tree
        def findKthSmallest(int k) {
            return findKthSmallestNode(this.root, k)
        }

        // Helper method for finding the k-th smallest element
        private def findKthSmallestNode(RBNode node, int k) {
            if (node == this.NIL_LEAF) {
                return null
            }
            def leftCount = node.left.count

            if (k <= leftCount) {
                return findKthSmallestNode(node.left, k)
            } else if (k > leftCount + 1) {
                return findKthSmallestNode(node.right, k - leftCount - 1)
            } else {
                return node.data
            }
        }
    }
}

