class MainClass {
    /**
     * Finds and returns the list of timestamps that include valid timezone abbreviations.
     * Valid abbreviations are "PST", "MST", "CDT", and "EDT".
     * A timestamp is considered applicable if it has exactly 4 parts (space-separated)
     * and the fourth part is one of the valid abbreviations.
     *
     * @param timestamps List of timestamp strings to be checked
     * @return List of applicable timestamps
     */
    static List<String> findApplicableTimestamps(List<String> timestamps) {
        def validAbbreviations = ["PST", "MST", "CDT", "EDT"]
        def applicableTimestamps = []
        timestamps.each { timestamp ->
            def parts = timestamp.split()
            if (parts.size() == 4 && validAbbreviations.contains(parts[3])) {
                applicableTimestamps.add(timestamp)
            }
        }
        return applicableTimestamps
    }
}

assertions:
void testFindApplicableTimestamps() {
    // Test case 1: Mixed valid and invalid timestamps
    def testCase1 = ["2023-01-01 12:00:00 PST", "2023-01-02 13:00:00 MST", "Invalid timestamp"]
    assert MainClass.findApplicableTimestamps(testCase1) == []

    // Test case 2: Timestamps with valid and invalid timezones
    def testCase2 = ["2023-01-03 14:00:00 CDT", "2023-01-04 15:00:00 EDT", "2023-01-05 16:00:00 UTC"]
    assert MainClass.findApplicableTimestamps(testCase2) == []

    // Test case 3: Timestamps with missing timezone and valid timezone
    def testCase3 = ["2023-01-06 17:00:00 PST", "2023-01-07 18:00:00", "2023-01-08 19:00:00 EDT"]
    assert MainClass.findApplicableTimestamps(testCase3) == []

    // Additional test case with actually valid timestamps
    def testCase4 = ["2023-01-01 00:00:00 PST", "2023-01-01 00:00:00 EDT"]
    assert MainClass.findApplicableTimestamps(testCase4) == ["2023-01-01 00:00:00 PST", "2023-01-01 00:00:00 EDT"]

    // Test case with no valid timestamps
    def testCase5 = ["Invalid", "2023-01-01 00:00:00 UTC"]
    assert MainClass.findApplicableTimestamps(testCase5) == []
}

testFindApplicableTimestamps()

