class MainClass {
    // separateWordsByLength separates words into a map where keys are word lengths
    // and values are lists of words of that length.
    // If the input list is empty, it returns an empty map.
    static def separateWordsByLength(List<String> words) {
        def lengthMap = [:]
        words.each { word ->
            def length = word.length()
            if (lengthMap.containsKey(length)) {
                lengthMap[length] << word
            } else {
                lengthMap[length] = [word]
            }
        }
        return lengthMap
    }
}
