class MainClass {
    /**
     * Computes the minimum absolute difference between the sum of two partitions
     * of the given task durations using dynamic programming.
     *
     * @param n The number of tasks.
     * @param tasks A list of task durations.
     * @return The minimum absolute difference between the two partitions.
     */
    static int minDifferencePartition(int n, List<Integer> tasks) {
        int totalSum = tasks.sum()
        int halfSum = totalSum / 2

        // Initialize a DP table where dp[i][j] is true if a subset of the first i tasks can sum to j
        boolean[][] dp = new boolean[n + 1][halfSum + 1]
        dp[0][0] = true

        for (int i = 1; i <= n; i++) {
            for (int j = 0; j <= halfSum; j++) {
                dp[i][j] = dp[i - 1][j]
                if (j >= tasks[i - 1]) {
                    dp[i][j] = dp[i][j] || dp[i - 1][j - tasks[i - 1]]
                }
            }
        }

        // Find the largest j <= halfSum for which dp[n][j] is true
        for (int j = halfSum; j >= 0; j--) {
            if (dp[n][j]) {
                return totalSum - 2 * j
            }
        }

        return totalSum  // Fallback, should not reach here if logic is correct
    }
}

