class DirectorySizeCalculator {
    /**
     * Calculates the total size of all files in the given directory and its subdirectories.
     * 
     * @param path Path of the directory as a File object
     * @return Total size of all files in bytes
     * @throws NotDirectoryException If the path is not a valid directory
     */
    static long calculateDirectorySize(File path) {
        if (!path.isDirectory()) {
            throw new NotDirectoryException("The path ${path.path} is not a valid directory")
        }

        long totalSize = 0
        path.eachFileRecurse { file ->
            if (file.isFile()) {
                totalSize += file.length()
            }
        }
        return totalSize
    }
}

// Custom exception class since Groovy doesn't have NotADirectoryError
class NotDirectoryException extends Exception {
    NotDirectoryException(String message) {
        super(message)
    }
}

