class MainClass {
    /**
     * Splits a string into a specified number of parts as evenly as possible.
     * If the number of parts is less than or equal to 0, returns an empty list.
     *
     * @param s The input string to be split.
     * @param numParts The number of parts to split the string into.
     * @return A list containing the split parts.
     */
    static List<String> splitString(String s, int numParts) {
        if (numParts <= 0) {
            return []
        }

        int partLen = s.length() / numParts
        int extraChars = s.length() % numParts

        List<String> parts = []
        int start = 0
        for (int i = 0; i < numParts; i++) {
            // Each part should have at least partLen characters
            // Distribute the remaining characters among the first 'extraChars' parts
            int end = start + partLen + (i < extraChars ? 1 : 0)
            parts.add(s.substring(start, end))
            start = end
        }

        return parts
    }
}

