class MainClass {
    // Finds the maximum combined difficulty where two distinct problems' difficulties sum to T
    // Parameters:
    //   N - number of problems (unused in the actual calculation)
    //   T - target sum to look for
    //   difficulties - list of problem difficulties
    // Returns:
    //   maximum combined difficulty that sums to T, or -1 if no such pair exists
    static int maxCombinedDifficulty(int N, int T, List<Integer> difficulties) {
        Map<Integer, Integer> difficultyMap = [:]
        int maxDifficulty = -1

        difficulties.each { d ->
            if (difficultyMap.containsKey(T - d)) {
                maxDifficulty = Math.max(maxDifficulty, d + (T - d))
            }
            difficultyMap[d] = difficultyMap.getOrDefault(d, 0) + 1
        }

        return maxDifficulty
    }
}

