class SubarraySumCounter {
    // countSubarraysWithSumBetween calculates the number of subarrays in a given list of integers
    // where the sum of elements is between a specified lower bound (l) and upper bound (r) inclusive.
    // The function uses a nested loop to check all possible subarrays and counts those that meet the sum condition.
    // If the current sum exceeds the upper bound, it breaks the inner loop early for efficiency.
    static int countSubarraysWithSumBetween(List<Integer> arr, int l, int r) {
        int n = arr.size()
        int count = 0

        for (int start = 0; start < n; start++) {
            int currentSum = 0
            for (int end = start; end < n; end++) {
                currentSum += arr[end]
                if (currentSum >= l && currentSum <= r) {
                    count++
                } else if (currentSum > r) {
                    break
                }
            }
        }
        return count
    }
}

