class MainClass {
    /**
     * Finds the flow path of a point (x, y) in a grid based on the lowest neighboring cell.
     * The flow path is determined by moving to the neighbor with the lowest height until no lower neighbor is found.
     *
     * @param grid A 2D list of integers representing the grid heights.
     * @param x The starting row index.
     * @param y The starting column index.
     * @return A tuple (x, y) representing the final position in the flow path.
     */
    static def flowPath(List<List<Integer>> grid, int x, int y) {
        def getNeighbors = { int xCoord, int yCoord ->
            def neighbors = []
            if (yCoord - 1 >= 0) {
                neighbors.add([xCoord, yCoord - 1])  // Left
            }
            if (yCoord + 1 < grid[0].size()) {
                neighbors.add([xCoord, yCoord + 1])  // Right
            }
            if (xCoord - 1 >= 0) {
                neighbors.add([xCoord - 1, yCoord])  // Top
            }
            if (xCoord + 1 < grid.size()) {
                neighbors.add([xCoord + 1, yCoord])  // Bottom
            }
            return neighbors
        }

        while (true) {
            def currentHeight = grid[x][y]
            def neighbors = getNeighbors(x, y)
            def minHeight = currentHeight
            def nextX = x
            def nextY = y

            neighbors.each { nx, ny ->
                if (grid[nx][ny] < minHeight) {
                    minHeight = grid[nx][ny]
                    nextX = nx
                    nextY = ny
                }
            }

            if (nextX == x && nextY == y) {
                break
            }
            x = nextX
            y = nextY
        }

        return [x, y]
    }
}

