class RangeUpdateQueries {
    /**
     * Performs range update operations on an array and answers queries about the final values.
     * Uses a difference array technique to efficiently apply range updates.
     *
     * @param n The size of the array
     * @param m The number of operations (unused in implementation but kept for interface consistency)
     * @param A The initial array
     * @param operations List of range update operations, each as [l, r, x] where:
     *        l is the 1-based left index, r is the 1-based right index, x is the value to add
     * @param q The number of queries (unused in implementation but kept for interface consistency)
     * @param queries List of 1-based indices to query
     * @return List of results for each query
     */
    static List<Integer> performRangeUpdatesAndQueries(int n, int m, List<Integer> A, 
                                                     List<List<Integer>> operations, 
                                                     int q, List<Integer> queries) {
        // Initialize difference array with n+1 elements (using 0-based indexing)
        List<Integer> diff = new ArrayList<>(Collections.nCopies(n + 1, 0))

        // Apply each range update operation to the difference array
        operations.each { op ->
            int l = op[0] - 1  // Convert to 0-based index
            int r = op[1] - 1  // Convert to 0-based index
            int x = op[2]

            diff[l] += x
            if (r + 1 < n) {
                diff[r + 1] -= x
            }
        }

        // Apply the difference array to compute the final array values
        for (int i = 0; i < n; i++) {
            if (i > 0) {
                diff[i] += diff[i - 1]
            }
            A[i] += diff[i]
        }

        // Answer the queries by collecting the requested elements
        List<Integer> result = []
        queries.each { k ->
            result.add(A[k - 1])  // Convert 1-based index to 0-based
        }

        return result
    }
}

