class MainClass {
    /**
     * Rotates a list of 2D points by a given angle in degrees around the origin.
     * 
     * @param points A list of [x, y] coordinates of points.
     * @param angle The angle of rotation in degrees.
     * @return A list of [x, y] coordinates of the points after rotation.
     */
    static List<List<Double>> rotatePoints(List<List<Double>> points, double angle) {
        // Convert angle from degrees to radians
        double theta = Math.toRadians(angle)

        // Calculate the rotation matrix components
        double cosTheta = Math.cos(theta)
        double sinTheta = Math.sin(theta)

        // Create a list to store the rotated points
        List<List<Double>> rotatedPoints = []

        // Rotate each point
        points.each { point ->
            double x = point[0]
            double y = point[1]

            // Apply the rotation matrix
            double xNew = x * cosTheta - y * sinTheta
            double yNew = x * sinTheta + y * cosTheta

            // Append the new point to the list
            rotatedPoints.add([xNew, yNew])
        }

        return rotatedPoints
    }
}

