class LongestSubarray {
    /**
     * Determines the length of the longest contiguous subarray where the 
     * absolute difference between any two elements is at most 1.
     *
     * @param arr List of integers
     * @return Length of the longest contiguous subarray
     */
    static int longestSubarrayWithAbsDiffAtMostOne(List<Integer> arr) {
        if (arr.isEmpty()) {
            return 0
        }

        int maxLength = 1
        int currentLength = 1
        for (int i = 1; i < arr.size(); i++) {
            if (Math.abs(arr[i] - arr[i-1]) <= 1) {
                currentLength++
                maxLength = Math.max(maxLength, currentLength)
            } else {
                currentLength = 1
            }
        }

        return maxLength
    }
}

