    assert LongestSubarray.longestSubarrayWithAbsDiffAtMostOne([5]) == 1
    assert LongestSubarray.longestSubarrayWithAbsDiffAtMostOne([1, 3, 5, 7, 9, 2, 4]) == 1
}

