class TreasureHunter {
    // Finds the shortest path from start (sx, sy) to treasure (tx, ty) in a given N x N grid
    // Returns the minimum number of steps, or -1 if not possible
    // Uses BFS (Breadth-First Search) algorithm to explore all possible paths level by level
    static int shortestPathToTreasure(int N, int sx, int sy, int tx, int ty, List<List<String>> grid) {
        // Directions for movement: up, down, left, right
        def directions = [[-1, 0], [1, 0], [0, -1], [0, 1]]

        // Check if starting or target points are walls
        if (grid[sx][sy] == '#' || grid[tx][ty] == '#') {
            return -1
        }

        // BFS initialization using a queue (simulated with a list)
        def queue = []
        queue.add([sx, sy, 0])  // [x, y, steps]
        def visited = new HashSet()
        visited.add("$sx,$sy")

        while (!queue.isEmpty()) {
            def current = queue.remove(0)
            int x = current[0]
            int y = current[1]
            int steps = current[2]

            // Check if we've reached the treasure
            if (x == tx && y == ty) {
                return steps
            }

            // Explore all four possible directions
            directions.each { dir ->
                int nx = x + dir[0]
                int ny = y + dir[1]
                String key = "$nx,$ny"

                if (nx >= 0 && nx < N && ny >= 0 && ny < N && !visited.contains(key) && grid[nx][ny] == '.') {
                    visited.add(key)
                    queue.add([nx, ny, steps + 1])
                }
            }
        }

        return -1
    }
}

