    static String isPalindromeLinkedList(List arr) {
        return arr == arr.reverse() ? 'yes' : 'no'
    }

    // Processes multiple test cases for palindrome checking.
    // Each test case consists of a size N and an array representing the linked list.
    // Returns a list of results ('yes' or 'no') for each test case.
    static List<String> processPalindromeTests(List<List> testCases) {
        List<String> results = []
        testCases.each { caseItem ->
            // Each caseItem is expected to be [N, arr], but N is not used in the check
            def arr = caseItem[1]
            results.add(isPalindromeLinkedList(arr))
        }
        return results
    }
}
void testPalindromeLinkedList() {
    def testCases = [
        [3, [1, 2, 1]],
        [4, [1, 2, 2, 1]],
        [5, [1, 2, 3, 4, 5]],
        [1, [1]],
        [0, []]
    ]

    assert MainClass.isPalindromeLinkedList([1, 2, 1]) == 'yes'
    assert MainClass.isPalindromeLinkedList([1, 2, 2, 1]) == 'yes'
    assert MainClass.isPalindromeLinkedList([1, 2, 3, 4, 5]) == 'no'
    assert MainClass.isPalindromeLinkedList([1]) == 'yes'
    assert MainClass.isPalindromeLinkedList([]) == 'yes'

    def expectedResults = ['yes', 'yes', 'no', 'yes', 'yes']
    def actualResults = MainClass.processPalindromeTests(testCases)
    assert actualResults == expectedResults
}

testPalindromeLinkedList()
