// Function to check if a string can become a palindrome by changing at most one character.
fun isAlmostPalindrome(s: String): Boolean {
    // Helper function to check if a substring from start to end is a palindrome.
    fun isPalindromeRange(start: Int, end: Int): Boolean {
        return s.substring(start, end + 1) == s.substring(start, end + 1).reversed()
    }

    var left = 0
    var right = s.length - 1

    while (left < right) {
        if (s[left] != s[right]) {
            // Check by removing one mismatched character either from the left or the right
            return isPalindromeRange(left + 1, right) || isPalindromeRange(left, right - 1)
        }
        left++
        right--
    }

    return true
}

