import java.util.PriorityQueue

// Function to find the minimum product path in a graph using Dijkstra's algorithm.
// The graph is represented as an adjacency list with nodes and weighted edges.
// The function returns the minimum product modulo 9987 from node 1 to node n.
fun findMinProductPath(n: Int, adj: Array<MutableList<Pair<Int, Int>>>): Int {
    val mod = 9987
    val dist = IntArray(n + 1) { Int.MAX_VALUE }
    dist[1] = 1
    val heap = PriorityQueue<Pair<Int, Int>>(compareBy { it.first })
    heap.add(Pair(1, 1))

    while (heap.isNotEmpty()) {
        val (currentDist, u) = heap.poll()
        if (u == n) {
            break
        }
        if (currentDist > dist[u]) {
            continue
        }
        for ((v, w) in adj[u]) {
            val newDist = (currentDist * w) % mod
            if (newDist < dist[v]) {
                dist[v] = newDist
                heap.add(Pair(newDist, v))
            }
        }
    }
    return dist[n] % mod
}

