// Function to decrypt a message encoded in a grid by reading columns and reconstructing rows.
fun decryptMessage(s: String): String {
    val n = s.length

    // Determine the number of rows (r) and columns (c) for the grid
    var r = 0
    var c = 0
    val maxPossibleR = Math.sqrt(n.toDouble()).toInt()
    for (possibleR in maxPossibleR downTo 1) {
        if (n % possibleR == 0) {
            val possibleC = n / possibleR
            if (possibleR <= possibleC) {
                r = possibleR
                c = possibleC
                break
            }
        }
    }

    // Split the encrypted message into C columns
    val columns = mutableListOf<String>()
    for (i in 0 until c) {
        val start = i * r
        val end = (i + 1) * r
        columns.add(s.substring(start, end))
    }

    // Reconstruct the original message by reading row-wise
    val original = StringBuilder()
    for (row in 0 until r) {
        for (col in 0 until c) {
            original.append(columns[col][row])
        }
    }

    return original.toString()
}

