// Function to check if a number is a magic number.
// A magic number is defined as a number whose first half digits are equal to the second half digits.
fun isMagicNumber(num: Int): Boolean {
    val s = num.toString()
    val length = s.length
    if (length % 2 != 0) {
        return false
    }
    val half = length / 2
    return s.substring(0, half) == s.substring(half)
}

// Function to check if a number consists of all '9's and has an odd number of digits.
fun isAllNinesOdd(num: Int): Boolean {
    val s = num.toString()
    return s.all { it == '9' } && s.length % 2 == 1
}

// Function to compute the number of turns required to reduce the input number to a magic number
// or an all-nines odd number, following the rules provided.
fun computeTurnsToMagicNumber(n: Int): Int {
    var currentNumber = n
    var turns = 0

    while (true) {
        if (isAllNinesOdd(currentNumber)) {
            break
        }
        if (isMagicNumber(currentNumber)) {
            val s = currentNumber.toString()
            val halfLength = s.length / 2
            currentNumber = s.substring(0, halfLength).toInt()
            turns += 1
        } else {
            currentNumber += 1
            turns += 1
        }
    }

    return turns
}

assertions:
fun testComputeTurnsToMagicNumber() {
    // Test cases from the original main function
    assert(computeTurnsToMagicNumber(123123) == 1)   // Magic number
    assert(computeTurnsToMagicNumber(999) == 0)      // All nines and odd
    assert(computeTurnsToMagicNumber(123456) == 641) // Not magic, not all nines
    assert(computeTurnsToMagicNumber(9999) == 1)     // All nines but even
    assert(computeTurnsToMagicNumber(1234) == 666)   // Not magic, not all nines
}

testComputeTurnsToMagicNumber()

