// twoSum checks if there are two distinct indices in the array 'nums' such that
// the sum of the elements at these indices equals 'target'.
// It uses a HashMap to store the elements and their indices for quick lookup.
fun twoSum(nums: IntArray, target: Int): Boolean {
    val numMap = HashMap<Int, Int>()
    for ((index, num) in nums.withIndex()) {
        val complement = target - num
        if (numMap.containsKey(complement)) {
            return true
        }
        numMap[num] = index
    }
    return false
}

