// manageQueues processes a list of commands to manage queues for different rides.
// Each command can be either "ARRIVE" or "LEAVE", followed by a ride ID and optionally a person ID.
// The function returns a list of strings representing the current state of each ride's queue.
fun manageQueues(commands: List<String>): List<String> {
    val queues = mutableMapOf<String, MutableList<String>>()

    for (command in commands) {
        val parts = command.split(" ")
        val action = parts[0]
        val rideId = parts[1]
        val personId = if (parts.size > 2) parts[2] else null

        // Initialize the queue for the ride if it doesn't exist
        if (!queues.containsKey(rideId)) {
            queues[rideId] = mutableListOf()
        }

        when (action) {
            "ARRIVE" -> {
                // Add the person to the ride's queue
                if (personId != null) {
                    queues[rideId]?.add(personId)
                }
            }
            "LEAVE" -> {
                // Remove the person from the ride's queue if they are in it
                if (personId != null && queues[rideId]?.contains(personId) == true) {
                    queues[rideId]?.remove(personId)
                }
            }
        }
    }

    // Prepare the result by formatting each ride's queue
    val result = mutableListOf<String>()
    for ((rideId, queue) in queues) {
        val queueStr = if (queue.isNotEmpty()) queue.joinToString(" ") else ""
        result.add("$rideId: $queueStr")
    }

    return result
}

