/**
 * Calculates the minimum energy (steps) required to move from the top-left corner to the bottom-right corner of a grid.
 * The grid consists of '.' (walkable path) and '#' (blocked path). The movement is allowed in 4 directions: up, down, left, right.
 *
 * @param N The number of rows in the grid.
 * @param M The number of columns in the grid.
 * @param grid The 2D grid representing the maze.
 * @return The minimum energy (steps) required to reach the destination, or -1 if the destination is unreachable.
 */
fun minimumEnergy(N: Int, M: Int, grid: Array<CharArray>): Int {
    // Check if the start or end points are blocked
    if (grid[0][0] == '#' || grid[N - 1][M - 1] == '#') {
        return -1
    }

    // Movement vectors for up, down, left, right
    val directions = arrayOf(-1 to 0, 1 to 0, 0 to -1, 0 to 1)

    // Create a queue for BFS initialized with the starting position (0, 0) and initial cost 0
    val queue = ArrayDeque<Triple<Int, Int, Int>>()
    queue.add(Triple(0, 0, 0))
    val visited = mutableSetOf<Pair<Int, Int>>()
    visited.add(0 to 0)

    while (queue.isNotEmpty()) {
        val (x, y, cost) = queue.poll()

        // If we've reached the bottom-right corner
        if (x == N - 1 && y == M - 1) {
            return cost
        }

        // Explore all possible moves
        for ((dx, dy) in directions) {
            val nx = x + dx
            val ny = y + dy

            // Check if the new position is valid and not blocked
            if (nx in 0 until N && ny in 0 until M && grid[nx][ny] == '.' && (nx to ny) !in visited) {
                visited.add(nx to ny)
                queue.add(Triple(nx, ny, cost + 1))
            }
        }
    }

    // If no path was found to the bottom-right corner
    return -1
}

