    // Test Case 1: Simple 2x2 grid with a clear path
    val grid1 = arrayOf(
        charArrayOf('.', '.'),
        charArrayOf('.', '.')
    )
    assert(minimumEnergy(2, 2, grid1) == 2)

    // Test Case 2: 3x3 grid with a blocked path
    val grid2 = arrayOf(
        charArrayOf('.', '.', '#'),
        charArrayOf('.', '#', '.'),
        charArrayOf('.', '.', '.')
    )
    assert(minimumEnergy(3, 3, grid2) == 4)

    // Test Case 3: 2x2 grid with blocked start or end
    val grid3 = arrayOf(
        charArrayOf('#', '.'),
        charArrayOf('.', '.')
    )
    assert(minimumEnergy(2, 2, grid3) == -1)

    // Test Case 4: 1x1 grid with a clear path
    val grid4 = arrayOf(
        charArrayOf('.')
    )
    assert(minimumEnergy(1, 1, grid4) == 0)
}

testMinimumEnergy()

