// Function to count the number of subarrays with at least one '1' in a binary array.
// The approach involves calculating the total number of subarrays and subtracting the number of subarrays with all zeros.
fun countSubarraysWithAtLeastOneOne(testCases: List<Pair<Int, List<Int>>>): List<Int> {
    val results = mutableListOf<Int>()

    for ((n, a) in testCases) {
        // Calculate the total number of possible subarrays in an array of size n
        val totalSubarrays = n * (n + 1) / 2
        var zeroCount = 0
        var zeroSubarrays = 0

        for (j in 0 until n) {
            if (a[j] == 0) {
                zeroCount += 1
            } else {
                // When encountering a '1', calculate the number of all-zero subarrays in the current zero sequence
                zeroSubarrays += zeroCount * (zeroCount + 1) / 2
                zeroCount = 0
            }
        }
        // Add the remaining zero sequences after the loop ends
        zeroSubarrays += zeroCount * (zeroCount + 1) / 2

        // The result is total subarrays minus the all-zero subarrays
        results.add(totalSubarrays - zeroSubarrays)
    }

    return results
}

assertions:
fun testCountSubarraysWithAtLeastOneOne() {
    val testCases = listOf(
        Pair(3, listOf(1, 0, 1)) to 5,
        Pair(4, listOf(0, 0, 0, 1)) to 4,
        Pair(5, listOf(1, 1, 1, 1, 1)) to 15,
        Pair(2, listOf(0, 0)) to 0
    )

    for ((input, expected) in testCases) {
        val result = countSubarraysWithAtLeastOneOne(listOf(input))
        assert(result[0] == expected) {
            "Failed for input ${input.second}. Expected $expected but got ${result[0]}"
        }
    }
    println("All tests passed!")
}

testCountSubarraysWithAtLeastOneOne()

